context("filterFeatures")test_that("filterFeatures", {  ns = getTaskFeatureNames(binaryclass.task)	f = getFeatureFilterValues(task=binaryclass.task, method="chi.squared")  expect_equal(names(f), ns)  expect_equal(filterFeatures(f), names(sort(f[f > 0], decreasing = TRUE)))    f = getFeatureFilterValues(task=binaryclass.task, method="random.forest.importance")  expect_equal(names(f), ns)  expect_equal(filterFeatures(f), names(sort(f[f > 0], decreasing = TRUE)))})