context("filterFeatures")test_that("filterFeatures", {  ns = getTaskFeatureNames(binaryclass.task)	f = getFeatureFilterValues(obj=binaryclass.df, target=binaryclass.target, method="chi.squared")  expect_equal(names(f), ns)  fdf = filterFeatures(obj=binaryclass.df, target=binaryclass.target,                        feat.importance = f, n = 8L)  expect_equal(fdf, getTaskData(binaryclass.task,                                 features=names(head(sort(f, decreasing = TRUE), 8))))    f = getFeatureFilterValues(obj=binaryclass.task, method="random.forest.importance")  expect_equal(names(f), ns)  fdf = filterFeatures(obj=binaryclass.task, feat.importance = f, threshold = 10)  expect_equal(fdf, subsetTask(binaryclass.task, features=names(f[f > 10])))})